#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string filename;
    std::cout << "Enter the name of the file: ";
    std::cin >> filename;
    std::ifstream file(filename);
    if (!file) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return 1;
    }
    std::string word;
    std::cout << "Enter the word to count: ";
    std::cin >> word;
