def lcs(x: str, y: str) -> str:
    m, n = len(x), len(y)
    @lru_cache(None)
    def dp(i, j):
        if i == m or j == n: return ""
        if x[i] == y[j]: return x[i] + dp(i + 1, j + 1)
        return max(dp(i + 1, j), dp(i, j + 1), key=len)
    return dp(0, 0)
