// Import the necessary Discord.js classes
const { Client, GatewayIntentBits, Collection } = require('discord.js');

// Create a new client instance
const client = new Client({ intents: [GatewayIntentBits.Guilds] });

// Array of command names and their functions
const commands = [
  { name: 'ban', function: async (interaction) => {
    // Get the user to be banned from the interaction options
    const user = interaction.options.getUser('user');
    // Get the reason for the ban from the interaction options (optional)
    const reason = interaction.options.getString('reason');
    // Ban the user and send a success message to the channel
    await interaction.guild.members.ban(user, { reason: reason });
    await interaction.reply(`${user.username} has been banned!`);
  } },
];

// Handle incoming messages from Discord
client.on('interactionCreate', async interaction => {
  if (!interaction.isChatInputCommand()) return;
  // Find the command that matches the name of the slash command used by the user
  const command = commands.find(cmd => cmd.name === interaction.commandName);
  if (!command) return;
  // Execute the function associated with the command and send a response to the user
  try {
    await command.function(interaction);
  } catch (error) {
    console.error(error);
    await interaction.reply('There was an error while executing this command!');
  }
});
```

