            size_t start = line.find("[") + 1;
            size_t end = line.find("]");
            html += "<a href=\"" + line.substr(start, end - start - 1) + "\">" + line.substr(end + 1) + "</a>";
        } else {
            html += line;
        }
    }
    return html;
}

