#include <iostream>

std::string markdownToHtml(const std::string& markdown) {
    // Convertir les balises Markdown en balises HTML
    std::string html = markdown;
    size_t pos = 0;
    while ((pos = html.find("*", pos)) != std::string::npos) {
        if (html.find("*", pos + 1) == pos + 1) {
            html.replace(pos, 1, "<em>");
            pos += 4; // Ajouter la longueur de la balise HTML pour avancer le curseur
        } else if (html.find("**", pos + 1) == pos + 2) {
            html.replace(pos, 2, "<strong>");
            pos += 7; // Ajouter la longueur de la balise HTML pour avancer le curseur
        } else {
            html.replace(pos, 1, "</em>");
            pos += 4; // Ajouter la longueur de la balise HTML pour avancer le curseur
        }
    }
    return html;
}
