std::string markdownToHTML(const std::string& markdown) {
    // Replace Markdown links with HTML links
    std::regex linkRegex("\\[([^\\]]*)\\]\\(([^)]*)\\)");
    std::string html = std::regex_replace(markdown, linkRegex, "<a href=\"$2\">$1</a>");

    // Replace Markdown emphasis with HTML emphasis tags
    std::regex emphasisRegex("(\\*|_)([^\\*_]+)\\1");
    html = std::regex_replace(html, emphasisRegex, "<em>$2</em>");

