#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main() {
    ifstream f("input.txt"); // Creates a file reader object from the input.txt file.
    string line; // Creates a variable that will be used as text containing the lines of the file.
    int count = 0; // Creates a variable that counts the specified word.
    while (getline(f, line)) { // Loop that reads the file line by line and determines the count of the specified word.
        if (line.find(count) != string::npos) { // If the word is found in the text, it counts the occurrences.
            count++; // Increases the count of occurrences.
        } else { // If the word is not found, there are no changes to the count.
            count = 0; // Resets the count to 0.
        }
    }
    cout << count; // Displays the count of occurrences.
