    if (!infile.is_open()) {
        cerr << "Could not open file" << endl;
        return EXIT_FAILURE;
    }

    // Read the file line by line
    string line;

    while (getline(infile, line)) {
        // Get the word and count how many times it appears
        size_t count = 0;

        // Split the line at each space and count each word
