```solidity
contract PreSaleContracts {
    address public owner;
    event TransferEvent(address from, address to, uint256 amount);

    function PreSaleContracts() external payable public override {
        owner(thisOwner);
    }

    function mintForPreSale() external payable public override {
        require(msg.sender == thisOwner, "Only the owner can mint");

        emit MintForPreSaleEvent();
