import pygame
from pygame.locals import *
from pygame.key import *
import time
import sys
import random

class Game:

    def __init__(self):
        pygame.init()
        self.screen = pygame.display.set_mode((800,600))
        self.fps = 0
        self.last_key = None

    def update(self):
        if self.last_key:
            self.fps += 1
            if self.last_key[K_ESCAPE]:
                self.fps = 0
                sys.exit(0)
        for event in pygame.event.get():
            if event.type == KEYDOWN:
                if event.key == K_ESCAPE:
                    self.last_key = None
                    self.fps = 0
                    sys.exit(0)
                elif event.key in KEYUP and event.key in KEYDOWN:
                    self.last_key = event.key
        self.screen.fill((255,255,255))
        self.screen.blit(self.background, None)
        pygame.display.flip()
        time.sleep(0.02)

    def draw(self):
        pygame.draw.rect(self.screen, black, (0,0,800), 1)
        pygame.draw.rect(self.screen, white, (100,100,400), 1)

        pygame.draw.line(self.screen, black, (100,100), (200,200), width=2)

        self._fps_text("FPS:  " + str(self._fps()))

        pygame.display.update()

    def render_text(self, text):
        self._fps_text(text)

    def get_fps(self):
        return self._fps

    def key_pressed(self, key):
        if key in KEYUP and key in KEYDOWN:
            return False

        return True

    def process_input(self):
        while True:
            for event in pygame.event.get():
                if event is KEYDOWN:
                    if event[KEY] in KEYUP:
                        break
                    elif event[KEY] in KEYDOWN:
                        return False
                elif event is KEYUP and event[KEY] in KEYDOWN:
                    break

            time.sleep(0)


