#include <fstream>
#include <string>

using namespace std;

// Read a text file line by line and count the number of occurrences of a specific word in the file.
int main()
{
    ifstream in("textfile.txt");
    if (!in.is_open())
    {
        cout << "Could not open file.\n";
        return EXIT_FAILURE;
    }

    int count = 0;
    string line;

    while (getline(in, line))
