#include <fstream>
#include <iostream>
#include <string>
#include <vector>
using namespace std;

int main() {
    // Lire le fichier
    ifstream fichier("mon_fichier.txt");

    if (!fichier.is_open()) {
        // Si le fichier n'est pas ouvert, lancer une erreur
        cerr << "Impossible d'ouvrir le fichier" << endl;
        return EXIT_FAILURE;
    }

    // Lire ligne par ligne
    vector<string> lignes;
    while (fichier.good()) {
        // Lire une ligne du fichier
        getline(fichier, ligne);

        // Créer une liste de mots à analyser
        vector<string> mots;

        // Créer un tableau de mots à compter
        map<string, int> mot_comptes;

        // Parcourir la liste des mots à analyser
        for (auto& ligne : lignes) {
            // Si le mot est dans la liste de mots à analyser
            if (mot_comptes.count(ligne)) {
                // Si le mot existe déjà dans la liste
                if (mot_comptes[ligne] != INT_MAX) {
                    // Mettre à jour la valeur du mot
                    mot_comptes[ligne]++;
                } else {
                    // Si ce n'est pas déjà dans la liste,
                    mot_comptes[ligne]++;
                }
            } else {
                // Sinon, créer une nouvelle entrée
                mot_comptes[ligne] = INT_MAX;
            }
        }

        // Afficher le nombre de fois où chaque mot apparaît
        for (auto& entrée : mot_comptes) {
            cout << entrée.first << "="
                 << entrée.second
                 << endl;
        }
    }

    // Clôturer le fichier
    fichier.close();

    // Sortir les résultats
    std::sort(lignes.begin(), lignes.end(),
             [](const string& a, const string& b)
             -> bool
             {
                 return a.length() > b.length();
             });

    // Afficher les résultats
    for (auto& ligne: lignes)
        cout << ligne;

    return EXIT_SUCCESS;
}

