#include <fstream>
#include <string>

int main()
{
    std::ifstream file("input.txt");
    std::string line;

    while (std::getline(file, line))
    {
        if (line.find("specific_word") != std::string::npos)
        {
            std::cout << line << " has the word 'specific_word' " << std::endl;
        }
    }

    return 0;
}
```

