class Game(pygame.sprite.Sprite):
    def __init__(self, window):
        super().__init__(window)

        self.x = 0
        self.y = 0

        self.block_list = pygame.sprite.Group(
            pygame.sprite.Group(
                pygame.Rect(self.x, self.y, 30, 30),
                pygame.Rect(self.x + 30, self.y, 30, 30),
                pygame.Rect(self.x, self.y + 30, 30, 30),
                pygame.Rect(self.x + 30, self.y + 30, 30, 30)
            )
        )

        for block in pygame.sprite.Group(self.block_list):
            block.set_colorkey((255,255,255))
            block.fill((255,255,255))

    def update(self):
        pass

    def draw(self):
        pygame.draw.rect(self.window, pygame.Color("white"), self.block_list)

    def handle_events(self):
        for event in pygame.event.get():
            if event.type == pygame.QUIT:
                self._running = False
                break
            if event.type == pygame.KEYDOWN and event.key in [pygame.K_ESCAPE]:
                self._running = False
                break

        return True

    def run(self):
        while True:
            for event in pygame.event.get():
                if event is None or event is pygame._display._quit:
                    break

            if event is not None and event in pygame._display._quit:
                break

            if self._running:
                self._update()
                self._draw()

            if event is not None:
                return event

