#include <fstream>
#include <iostream>

using namespace std;

int main()
{
    ifstream fichier("cours.txt");

    string mot;
    int compteur = 0;

    while (getline(fichier, mot))
    {
        if (mot.find("le cours") != string::npos)
        {
            compteur++;
        }
    }

    cout << "Le mot \"le cours\" est présent dans le fichier " << endl;
    cout << "Il y a " << compteur << endl;

    return 0;
}

