        cerr << "No se encuentra el archivo." << endl;
        return EXIT_FAILURE;
    }

    // Leemos el archivo hasta que lleguemos al final
    while (getline(archivo, nombre_archivo)) {

        // Creamos una variable para almacenar los datos del archivo
        string dato;

        // Leemos la línea del archivo
        getline(archivo, dato);

        // Verificamos si existe la palabra que queremos contar
        if (dato.find("palabra") != string::npos) {

            // Leemos la cantidad de veces que se encuentra la palabra
            int numero_ocorrencias = dato.find("palabra") + strlen("palabra") - dato.find(":") + 1;

            // Iniciamos un contador con la cantidad de ocurrencias
