#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
  ifstream inFile;
  string word, line;
  int count = 0;
  cout << "Entrez le nom du fichier : ";
  cin >> word;
  inFile.open(word.c_str());
  if (inFile.is_open()) {
    while (!inFile.eof()) {
      getline(inFile, line);
      if (line.find(word) != string::npos) {
        count++;
