function findCommonElements(array1, array2) {
    // Initialiser en tom matrise for å lagre felles elementer
    const commonElements = [];

    // Gå gjennom hver element i array1
    for (let i = 0; i < array1.length; i++) {
        // Gå gjennom hver element i array2
        for (let j = 0; j < array2.length; j++) {
            // Sjekk om elementene er like, og legg til dem i commonElements-matrisen dersom de er like
            if (array1[i] === array2[j]) {
                commonElements.push(array1[i]);
            }
        }
    }

    // Returner commonElements-matrisen med felles elementer
    return commonElements;
}
