import java.util.*;

public class CommonElements {
    public static void main(String[] args) {
        int[] array1 = {1, 2, 3, 4, 5};
        int[] array2 = {2, 4, 6, 8};

        // Skapa en hashset som innehåller de gemensamma elementen i båda arrayerna
        HashSet<Integer> commonElements = new HashSet<>();
        for (int element : array1) {
            if (Arrays.asList(array2).contains(element)) {
                commonElements.add(element);
            }
        }
        // Skriv ut de gemensamma elementen i en lista med kommatecken emellan dem
        System.out.println(String.join(", ", commonElements));
    }
}

