```
#include <fstream>
#include <iostream>
#include <string>
#include <cstdlib>

using namespace std;

int main(int argc, char *argv[]) {
    ifstream inFile;
    string word;
    int count = 0;

    if (argc != 3) {
        cout << "Usage: " << argv[0] << " <input file> <word>\n";
        exit(1);
    }

    inFile.open(argv[1]);
    if (!inFile) {
        cout << "Error opening input file: " << argv[1] << "\n";
        exit(1);
    }
    word = argv[2];

    while (!inFile.eof()) {
        string line;
        getline(inFile, line);
        if (line.find(word) != string::npos) {
            count++;
        }
    }
    cout << "Word " << word << " occurred " << count << " times.\n";
}    
```

