def fib(n):
    if n == 0 or n == 1:
        return n
    else:
        memo = {}
        return fib_helper(n, memo)

def fib_helper(n, memo):
    if n in memo:
        return memo[n]
    elif n == 0 or n == 1:
        memo[n] = n
        return n
    else:
        value = fib_helper(n-1, memo) + fib_helper(n-2, memo)
        memo[n] = value
        return value
