#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::ifstream inFile("file.txt");
    std::string targetWord;
    int count = 0;

    // Get the target word from the user
    std::cout << "Enter the target word: ";
    std::cin >> targetWord;

    // Read the file line by line
    std::string line;
    while (std::getline(inFile, line)) {
        // Count the number of occurrences of the target word in each line
        int numOccurrences = 0;
        size_t pos = 0;
        while ((pos = line.find(targetWord, pos)) != std::string::npos) {
            numOccurrences++;
            pos += targetWord.length();
        }
        count += numOccurrences;
    }
    inFile.close();

    // Print the total number of occurrences of the target word in the file
    std::cout << "The total number of occurrences of \"" << targetWord << "\" in the file is: " << count << std::endl;
}
