#include <fstream>
#include <string>
#include <iostream>

int main() {
    // Tekstfilens navn og ordet du vil telle forekomster av
    std::string filename = "tekstfil.txt";
    std::string word = "eksempelord";
    int word_count = 0;

    // Åpne tekstfilen for lesing
    std::ifstream file(filename);

    // Hvis filen kunne åpnes for lesing...
    if (file.is_open()) {
        // Les linje for linje fra filen til en streng
        std::string line;
        while (getline(file, line)) {
            // Tell antall forekomster av "word" i hver linje og øk "word_count" med dette tallet
            word_count += line.count(word);
        }
        // Filen er nå lest. Lukk den.
        file.close();
    } else {
        // Filen kunne ikke åpnes, så skriv ut en feilmelding og avslutt programmet med feilkode (1)
        std::cout << "Kunne ikke åpne fil: " << filename << std::endl;
        return 1;
    }
    // Skriv ut antall forekomster av "word" i tekstfilen og avslutt programmet med sukseskode (0)
    std::cout << word << " fant i " << filename << ": " << word_count << std::endl;
    return 0;
}

