def is_prime(number):
    # Använd en tidsbesparande metod för att avgöra primtal
    if number <= 3:
        return number > 1
    if number % 2 == 0 or number % 3 == 0:
        return False
    i = 5
    while i * i <= number:
        if number % i == 0:
            return False
        i += 6 - i if i % 2 == 0 else 2
