#include <iostream>
#include <fstream>
#include <string>

int main() {
    // The word to count occurrences of
    std::string wordToCount = "example";

    // Open the text file for reading
    std::ifstream inputFile("file.txt");

    // Initialize the counter to 0
    int wordCount = 0;

    // Read each line of the file
    std::string line;
    while (std::getline(inputFile, line)) {
        // Count the number of occurrences of the word in the current line
        int wordOccurrencesInLine = 0;
        size_t pos = 0;
        while ((pos = line.find(wordToCount, pos)) != std::string::npos) {
            wordOccurrencesInLine++;
            pos += wordToCount.length();
        }
        // Increment the total count by the number of occurrences in the current line
        wordCount += wordOccurrencesInLine;
    }
    // Close the input file stream
    inputFile.close();
    // Print the total number of occurrences of the word in the file to the console
    std::cout << "There were " << wordCount << " occurrences of '" << wordToCount << "' in the file." << std::endl;
}

