#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    ifstream inFile("example.txt");
    string word, line;
    int wordCount = 0;
    while (getline(inFile, line)) {
        istringstream iss(line);
        while (iss >> word) {
            if (word == "hello") {
                wordCount++;
            }
        }
    }
    cout << "Anzahl der Vorkommnisse von 'hello' in der Datei: " << wordCount << endl;
    return 0;
}

