def fib(n):
    memo = {}
    def _fib(n):
        if n in memo:
            return memo[n]
        if n == 0 or n == 1:
            return n
        result = _fib(n-1) + _fib(n-2)
        memo[n] = result
        return result
    return _fib(n)
