```c++
#include <iostream>

int fib(int n) {
  if (n <= 1) {
    return n;
  } else {
    int fibNMinus1 = fib(n - 1);
    int fibNMinus2 = fib(n - 2);
    return fibNMinus1 + fibNMinus2;
  }
}

int main() {
  int n;
  std::cin >> n;
  std::cout << "Det " << n << ":e Fibonacci-talet är " << fib(n) << ".";
