#include <iostream>
#include <string>
#include <vector>
#include <iterator>
#include <algorithm>

using namespace std;

int main() {
    string text = "This is a sample text with the word \"dog\" appearing multiple times.";
    vector<string> words = split(text, ' '); // Split the text into words.
    int count = std::count_if(words.begin(), words.end(), [](const string& w) { return w == "dog"; }); // Count the number of occurrences of the word "dog".
