// Verwendet eine zufällige Tupel-Liste zum Berechnen des Schnittpunkts
// und eines Abstandes für die Komparation.
// Gibt einen Schnittpunkt zurück, der an der Position des ersten gemeinsamen Elements steht.
// Es gibt zwei Argumente: ein Array A und ein Array B.
function findCommonElements(a: number[], b: number[]) {
    const len = a.length;
    const blen = b.length;
    let s1 = new Array(len + 1).fill(null);
    let s2 = new Array(blen + 1).fill(null);
    let dist = new Array(len + 1).fill(null);

    for (let i = 0; i < len; i++) {
        for (let j = 0; j < blen; j++) {
            if (a[i] == b[j]) {
                s1[i] = a[i];
                s2[j] = b[j];
                dist[i] = i - j; // Abstand wird als Offset berechnet, um die Schnittpunkte zu ermitteln.
                break; // Gibt den ersten gemeinsamen Punkt zurück. 
            } else { 
                dist[i] = len; // Distanz von nicht-gleichem Element berechnet. 
            }                                                                                         }     }     return s1;    }   // Gibt den Schnittpunkt zurück für das erste gemeinsame Element.

