```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    // Open the text file.
    ifstream in("input.txt");
    if (!in) {
        cerr << "Could not open input file" << endl;
        return 1;
    }

    // Read the lines from the file.
    vector<string> lines;
    string line;
    while (getline(in, line)) {
        lines.push_back(line);
    }

    // Count the number of occurrences of a specific word in the file. 
    int occurences = 0;
    for (int i = 0; i < lines.size(); i++) {  
        if (lines[i].find("word") != string::npos) {  // Search for "word" in each line and increment the occurences counter. 
            occurences++; 
        } 
    } 

    // Print the number of occurrences. 
    cout << "The word \"" << "word" << "\" occurs " << occurences << " times in the file." << endl; 

    return 0;  // Successful execution. 
}  // End main function.

