```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <unordered_map>
#include <algorithm>

using namespace std;

// 检查输入文件中所有单词的出现次数。
void checkWordCount(const string& filePath) {
    ifstream in(filePath);
    string line;
    unordered_map<string, int> wordCount; // 单词出现次数映射。
    while (getline(in, line)) {
        auto split = split(line, ' '); // 按空格分割单词。
        for (int i = 0; i < split.size(); i++) { // 遍历每一个单词。
            auto word = split[i]; // 获取单词。
            if (wordCount.count(word) == 0) { // 如果字典中没有此字，则添加一条记录。
                wordCount[word] = 0; // 初始化记录值为 0。
            }
            wordCount[word]++; // 更新记录值。
        }
    }
    for (auto& [key, count] : wordCount) { // 遍历字典中的每一条记录。
        cout << key << ": " << count << endl; // 打印出每一条记录。
    }
}
