def common_elements(array1, array2):
    # Declare variables to store the common elements and their frequencies.
    common_elements = []
    common_frequency = {}
    for element in array1:
        if element in array2:
            if element not in common_elements:
                common_elements.append(element)
                common_frequency[element] = 1
            else:
                common_frequency[element] += 1
    # Sort the common elements by frequency.
    sorted_common_elements = sorted(common_frequency, key=common_frequency.get, reverse=True)
    # Print the common elements and their frequencies.
    for element, frequency in sorted_common_elements:
