```c++
#include <iostream>
#include <fstream>
#include <string>
#include <map>

using namespace std;

int main() {
    // Datei-Variable:
    string file_name = "input_file.txt";
    ifstream file(file_name);
    // Wort-Variable:
    string word = "word_to_count";
    // Wörterbuch-Variable:
    map<string, int> word_count;
    while (file) {
        // Lesen eines Zeilenwerts:
        string line = file.getline();
        // Vorkommen des Wortes im Zeilenwert:
        for (int i = 0; i < line.length(); i++) {
            if (line[i] == word[0]) {
                // Erhöhen der Anzahl des Wortes im Wörterbuch:
                word_count[word]++;
                break;
            }
        }
    }
    // Ausgabe der Anzahl der Vorkommnisse des Wortes im Wörterbuch:
    for (auto& entry : word_count) {
        cout << entry.first << ": " << entry.second << endl;
    }
    return 0;
}```

