    fibs[1] = 1;

    // Fill the remaining elements of the vector with the Fibonacci numbers.
    for (int i = 2; i < fibs.size(); i++) {
        fibs[i] = fibs[i - 1] + fibs[i - 2];
    }

    // Print the values of the Fibonacci sequence in reverse order.
