#include <fstream>
#include <string>
#include <iostream>

using namespace std;

int main() {
    ifstream file("input_file.txt");
    if(!file) {
        cout << "Unable to open input file" << endl;
        return 1;
    }
    int count = 0;
    string line;
    while(getline(file, line)) {
        if(line.find("my_word") != string::npos) {
            count++;
        }
    }
    cout << "The number of occurrences of the word 'my_word' in the file is " << count << endl;
}
