#include <fstream>
#include <string>

using namespace std;

int main(int argc, char** argv) {
    ifstream inputFile("wordcount.txt");

    if (!inputFile.is_open()) {
        cout << "Can't open file!" << endl;
        return 1;
    }

    string line;
    while (getline(inputFile, line)) {
        size_t wordCount = line.find(argv[1]);
        if (wordCount != string::npos)
            cout << argv[1] << " occured in the file: " << wordCount << endl;
    }

    inputFile.close();
    return 0;
}

