#include <iostream>

int main() {
    std::cout << "Enter the first number of the Fibonacci sequence: ";
    int firstNumber;
    std::cin >> firstNumber;
    int secondNumber;
    std::cin >> secondNumber;

    // Calculate the first two numbers of the Fibonacci sequence:
    int first = firstNumber;
    int second = secondNumber;

    for (int i = 0; i < secondNumber; i++) {
        int thirdNumber;
        thirdNumber = first + second;
        std::cout << thirdNumber << std::endl;

        // Calculate the new third number:
        first = second;
        second = thirdNumber;
    }

    std::cout << std::endl;

    return EXIT_SUCCESS;
}
```

