/**
 * Implementiert ein Programm, um die gemeinsamen Elemente in zwei Arrays zu finden, ohne zusätzliche Datenstrukturen zu verwenden.
 */
template <typename T>
void findCommonElements(std::vector<T>& firstArray, std::vector<T>& secondArray) {
    // Liste, die alle gemeinsamen Elemente enthält
    std::vector<std::pair<size_t, size_t>> commonElements;
    // Positions der Elemente
    size_t i = 0;
    size_t j = 0;
    // Traverse die beiden Arrays
    while (i < firstArray.size() && j < secondArray.size()) {
        // Falls ein Element gleicher Größe vorhanden ist, ersetzen wir es
        if (firstArray[i] == secondArray[j]) {
            commonElements.push_back(std::make_pair(i, j));
            i++; j++;
