Voici un exemple de programme en C++ qui lit un fichier texte ligne par ligne et compte le nombre d'occurrences d'un mot spécifique dans le fichier :

#include <fstream>

using namespace std;

int main()
{
    // Obtient la référence à l'objet de la classe ifstream qui permet d'ouvrir le fichier.
    ifstream fichier("texte.txt");

    // Ligne par ligne, lit chaque ligne du fichier et affiche le contenu de la ligne.
    for(string ligne; getline(fichier, ligne);)
    {
        cout << ligne << endl;
    }

    // La variable chaîne indique où se trouve le mot à compter.
    string mot = "exemple";

    // Recherche le mot dans chaque ligne du fichier et affiche le nombre d'occurrences.
    for(string mot_recue; getline(fichier, mot_recue);)
    {
        // Si le mot se trouve dans la chaîne, ajoute son nombre d'occurrences à la valeur précédente.
        if(mot_recue == mot)
            cout << mot_recue << " " << count(mot_recue, mots) << endl;
        else
            cout << mot_recue << endl;
    }

    // Ferme le fichier.
    fclose(fichier);
    return 0;
}

// Définit une fonction qui renvoie le nombre d'occurrences d'un mot dans une chaîne.
int count(string mot, string mots)
{
    // Définit une variable qui stockera le nombre total d'occurrences de mots.
    int total_occurrences = 0;

    // Récupère tous les mots contenus dans mots et ajoute leur nombre d'occurrences au total.
    for(string word: mots)
    {
        // Si le mot est égal au mot stocké dans la variable mot_recue,
        // on ajoute son nombre d'occurrences au total.
        if(word == mot)
            total_occurrences += count(word, mots);
    }

    // Retourne le nombre total d'occurrences du mot stocké dans la variable mot_recue.
    return total_occurrences;
}

// Définit une fonction qui retourne la valeur de la variable chaîne indiquant où se trouve le mot à compter.
string getline(ifstream& fichier, string& mot)
{
    // Lire une ligne du fichier et renvoyer sa valeur dans la variable chaîne.
    string line;
    getline(fichier, line);

    if(!fichier.eof())
    {
        cin >> line;

        while(!cin.eof())
        {
            if(line==mot)
                break;
            cin.get();
        }
    }

    return line;
}

