#include <iostream>
#include <fstream>

using namespace std;

int main() {
    ifstream file("example.txt");

    int n;
    int counter;
    file >> n;

    for(int i = 0; i < n; i++) {
        file.seekg(i, std::ios::beg);

        if(file.peek() == EOF)
            break;

        file >> counter;
        if(counter == 'a')
            cout << i << endl;
    }

    return EXIT_SUCCESS;
}
```
