def fib_dyn_progr(n):
    if n <= 1:
        return 1
    elif n == 2:
        return 1
    elif n == 3:
        return 1
    elif n > 3:
        if n % 2 == 0:
            return fib_dyn_progr(n-2) + fib_dyn_progr(n-4)
