```cpp
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    ifstream file("input.txt"); // Open the input file
    string word; // Variable for the word to count
    int word_occurrences; // Variable for counting the number of occurrences of the word
    int count = 0; // Initial count for total occurrences of the word in the file
    while (file >> word) { // Read each line of the input file into a string variable word
        ++count; // Increase count by one
        if (word == "test") { // If the line contains the word "test", increment word_occurrences by one
            word_occurrences++; // Update the count for this occurrence of "test" in this line
        }
    }
    cout << "Word \"test\" appears " << count << " times in " << file.str().size() << endl; // Print out total occurrences and total length of file content
    cout << "Average occurrence of \"test\" is " << (int)(count / file.str().size()) << endl; // Calculate average occurrence and print it out
}
```

