#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main() {
    ifstream infile;
    string word, line;
    int count = 0;
    // Obteniu l'arquitració del fitxer en el qual contem camps amb la paraula a buscar.
    cout << "Introdueixi l'arquitració del fitxer amb la paraula a buscar: ";
    cin >> infile;
    // Llegeixi cada línia del fitxer.
    while (getline(infile, line)) {
        count++; // Contem la quantitat d'aparicions de la paraula a buscar.
        // Busca la paraula a buscar en la línia.
        for (auto& w : line) { // Així evitem errors per problemes de cadenes amb punts en llocs inesperats.
            if (w == word) break; // Si s'ha trobat la paraula a buscar, es quita el camí de les altres aparicions.
        }
    }//Fes que acabi la lectura del fitxer.
