```c++
#include <iostream>
#include <fstream>
#include <string>
#include <vector>

using namespace std;

int main() {
    ifstream file;
    string word;
    int word_count = 0;
    file.open("textfile.txt");
    while (getline(file, word)) {
        if (word.find(word) != string::npos) {
            word_count++;
        }
    }
    cout << "Found " << word_count << " instances of " << word << endl;
}
