import math

def fib_recursive(n):
    """Returns the n-th Fibonacci number."""
    if n == 0:
        return 0
    elif n == 1:
        return 1
    else:
        return fib_recursive(n - 1) + fib_recursive(n - 2)

def fib_dynamic(n):
    """Returns the n-th Fibonacci number using dynamic programming."""
    dp_matrix = [[0 for _ in range(n+1)] for _ in range(n+1)]
    dp_matrix[0][0] = 0
    dp_matrix[1][0] = 1
    for i in range(2, n+1):
        for j in range(i, n+1):
            dp_matrix[i][j] = dp_matrix[i-1][j-1] + dp_matrix[i-1][j]
    return dp_matrix[-1][-1]

print("The 3rd Fibonacci number is " + str(fib_dynamic(3)))

