void leerArchivo(string nombreArchivo, string palabraBuscada) {
    ifstream archivo(nombreArchivo.c_str());
    if (!archivo.is_open()) {
        cout << "Archivo no encontrado: " << nombreArchivo << endl;
        return;
    }
    string linea;
    while (getline(archivo, linea)) {
        if (linea.find(palabraBuscada) != std::string::npos) {
            cout << linea << endl; // Imprime cada línea que contiene la palabra buscada.
