    ifstream infile("filename.txt"); // 使用 ifstream 对读取文件的代码
    string s;                      // 定义一个 string s 对逐行读取的字符串
    vector<string> count(20);      // 定义一个vector<string> count 对计算出现次数的单词
    while (getline(infile, s)) {   // 逐行读取文件中的字符串，然后将其存储到 s 变量中
        for (int i = 0; i < s.size(); i++) { // 将字符串 s 中的字符遍历，然后将其遍历到字符的次数相加
            count[s[i]].push_back(1);     // 将单词出现次数相加，然后将其存储到count[s[i]]中
        }
    }
    for (int i = 0; i < count.size(); i++) { // 遍历 count vector，输出出现次数最高的单词
        if (count[i].size() > count[i-1].size()) {   // 使用 max() 函数对 count vector中的单词进行比较，然后输出最高的值，这里使用大于运算符和整数排序，可以保证输出最高的单词。
            cout << i + 1 << " " << count[i][0] << endl;   // cout<<i+1<<" "<<count[i][0]<<endl;输出 i +1和单词名称。
        }
    }
}
