#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    // 定义变量，用于存储文件名和单词
    string filename, word;
    int count = 0;

    // 获取文件名和单词
    cout << "Enter the file name: ";
    cin >> filename;
    cout << "Enter the word: ";
    cin >> word;

    // 打开文件并逐行读取内容
    ifstream inFile(filename);
    string line;
    while (getline(inFile, line)) {
        // 查找单词并计算出现次数
        count += line.count(word);
    }

    // 输出结果
    cout << "The word " << word << " appears " << count << " times in the file." << endl;
}

