#include <iostream>

// Funktion som returnerar true om arrayen innehåller elementet, annars false
bool contains(int arr[], int size, int element) {
    for (int i = 0; i < size; i++) {
        if (arr[i] == element) {
            return true;
        }
    }
    return false;
}

// Funktion som hittar de gemensamma elementen i två arrayer utan att använda några extra datastrukturer
void findCommonElements(int arr1[], int size1, int arr2[], int size2) {
    for (int i = 0; i < size1; i++) {
        if (contains(arr2, size2, arr1[i])) {
            std::cout << arr1[i] << " är ett gemensamt element." << std::endl;
        }
    }
}
```

